package ru.mifiSoul.MultiBankApp.infrastructure.config.initApp;

import lombok.AllArgsConstructor;
import org.springframework.boot.CommandLineRunner;
import org.springframework.stereotype.Component;
import ru.mifiSoul.MultiBankApp.database.entity.Bank;
import ru.mifiSoul.MultiBankApp.database.entity.Role;
import ru.mifiSoul.MultiBankApp.database.entity.RoleEntity;
import ru.mifiSoul.MultiBankApp.database.repository.BankRepository;
import ru.mifiSoul.MultiBankApp.database.repository.RoleRepository;

import java.util.Base64;

@Component
@AllArgsConstructor
public class AppInitializer implements CommandLineRunner {

    private RoleRepository roleRepository;
    private BankRepository bankRepository;

    @Override
    public void run(String... args) throws Exception {
        var role = new RoleEntity();
        role.setRole(Role.ROLE_USER);
        roleRepository.save(role);

        role = new RoleEntity();
        role.setRole(Role.ROLE_ADMIN);
        roleRepository.save(role);

        var vBank = new Bank();
        vBank.setName("vbank");
        vBank.setUrl("vbank.open.bankingapi.ru");
        var pictureBase64 = "";
        vBank.setPicture(Base64.getDecoder().decode(pictureBase64));
        bankRepository.save(vBank);

        var aBank = new Bank();
        aBank.setName("abank");
        aBank.setUrl("abank.open.bankingapi.ru");
        pictureBase64 = "";
        aBank.setPicture(Base64.getDecoder().decode(pictureBase64));
        bankRepository.save(aBank);

        var sBank = new Bank();
        sBank.setName("sbank");
        sBank.setUrl("sbank.open.bankingapi.ru");
        pictureBase64 = "";
        sBank.setPicture(Base64.getDecoder().decode(pictureBase64));
        bankRepository.save(sBank);



    }
}
